def occurrences(Region, Pattern):
    n = len(Region)
    k = len(Pattern)
    num = 0
    for i in range(n - k + 1):
        if Region[i:i + k] == Pattern:
            num = num + 1
    return num
region =""
pat1 = "ATGATCAAG"
pat2 = "CTTGATCAT"
coun1 = occurrences(region, pat1)
coun2 = occurrences(region, pat2)
print(coun1 + coun2)
